`timescale 1ns/1ps

module sign;
    // Parameters for the testbench
    localparam int SK_WIDTH = 8 * (32 + 32 + 64 + 32 * (((Dilithium_pkg::l + Dilithium_pkg::k) * ($clog2(2 * Dilithium_pkg::eta)+1)) 
                                                   + (Dilithium_pkg::d * Dilithium_pkg::k))) - 1;

    // Testbench signals
    logic [SK_WIDTH:0] sk;
    logic [255:0] K;
    logic [511:0] tr;
    logic clk;
    logic rst;

    // Clock generation
    initial begin
        clk = 0;
        forever #0.01 clk = ~clk; // 100 MHz clock
    end

    // DUT instantiation
    sign_internal uut (
        .sk(sk),
        .K(K),
        .tr(tr),
        .clk(clk),
        .rst(rst)
//        .s1(s1),
    );

    // Testbench logic
    initial begin
        // Reset sequence
        rst = 1;
        #1 rst = 0;

        // Test case 1: Initialize inputs
        sk = 39168';
        #1000;

        $finish;
    end



endmodule
