module verifyInternal_tb;
    // Define parameters inside the testbench
    parameter int PK_SIZE = 20736;  // 2592 bytes
    parameter int MSG_MAX_SIZE = 27672; // Maximum message size
    parameter int SIG_SIZE = 37016;  // 4627 bytes
    
    logic clk;
    logic rst;
    logic [PK_SIZE-1:0] pk;  
    logic [MSG_MAX_SIZE-1:0] M;
    logic [SIG_SIZE-1:0] sigma;  
    logic valid;  // Assume 'valid' is an output from the DUT
    logic initial_start;

    // Instantiate the Device Under Test (DUT)
    verifyInternalModule #(
        .PK_SIZE(PK_SIZE),  //  2592  bytes
        .MSG_MAX_SIZE(MSG_MAX_SIZE),    // Maximum message size (adjustable)
        .SIG_SIZE(SIG_SIZE)  
    ) dut (
        .clk(clk),
        .rst(rst),
        .pk(pk),
        .M(M),
        .sigma(sigma),
        .valid(valid),
        .initial_start(initial_start)
    );

    // Clock generation (Toggle every 0.5 time units)

    // Testbench sequence
    initial begin
        clk = 1; // Initialize clock 
        rst = 1'b1;
        pk = 0;
        M = 0;
        sigma = 0;
        initial_start = 1'b0;

        #1;  // Wait for some time before finishing the simulation
        rst=1'b0;
        initial_start = 1'b1;
//               pk = 20736';
//               pk = 20736';
//               pk = 20736';
//               reverse
//                 pk = 20736';
//         withoutout reverse
               
//                   pk = 20736';
               
//               big endian reverse
                   pk = 20736';
                  M = 27672';
               
//               athstia python direct input
                sigma = 37016';
                             
              
        #140;

        $finish;
    end
    
    always begin 
    #0.001 clk = ~clk;
    end

    // Dump waveforms for debugging
    initial begin
        $dumpfile("verifyInternal_tb.vcd");
        $dumpvars(0, verifyInternal_tb);
    end
endmodule