`timescale 1ns / 1ps


module topp(    
    input logic clk,
    input logic rst,
    output logic led
   );
   
   logic [1633:0] datain;
   logic [4095:0] digest;
   logic [4095:0] check;
   assign datain = 1634';
   assign check = 4096';

//   assign datain = 120'h726E676A6A656A6A726A676A657268 ;
//   assign check = 256'hdfde6395b8647257f66c3ccffd567170e6c6b76811cb0293679d459d3d5e976e ;

   
   sponge #(
    .msg_len(1634),
    .d_len(4096),
    .capacity(512),
    .r(1600 - 512)
) shake(
       .clk(clk),
       .reset(rst),
       .start(1'b1),
       .message(datain),
       .z(digest)
   );
   
   always_ff @(posedge clk)begin
     if (digest == check) begin
            led <= 1'b1;  // Assign 1 to 'led'
        end
        else begin
            led <= 1'b0;  // Assign 0 to 'led'
        end
   end
//   assign led = digest[0:5];
endmodule
